import { colors } from 'utils/style';
import { PlotData, Color, MarkerSymbol, HoverLabel, ColorScale } from '../../index';
import { ScatterSelectedMarker } from './scatter';

// See https://github.com/plotly/plotly.js/blob/master/src/traces/box/attributes.js
export type SankeyHoverInfo = 
    | 'all'
    | 'none'
    | 'skip';

export interface SankeyData extends PlotData {
    type: 'sankey';
    orientation: 'v' | 'h';
    arrangement: "snap" | "perpendicular" | "freeform" | "fixed";
    valueformat: string;
    valuesuffix: string;
    textfont: Partial<Font>;
    node: Partial<{
        label: string[];
        groups: string | number[] | string[];
        x: number[];
        y: number[];
        color: Color;
        line: Partial<{
            color: Color,
            width: number,
        }>;
        pad: number;
        thickness: number;
        hoverinfo: SankeyHoverInfo;
        hoverlabel: HoverLabel; // needs editType override,
        hovertemplate: PlotData['hovertemplate']
    }>;
    link: Partial<{
        arrowlen: number;
        label: Array<string>;
        color: Color;
        customdata: Datum[] | Datum[][];
        line: Partial<{
            color: Color;
            width: number;
        }>;
        source: number[];
        target: number[];
        value: number[];
        hoverinfo: SankeyHoverInfo;
        hoverlabel: Partial<HoverLabel>; // needs editType override,
        hovertemplate: string | string[];
        colorscales: ColorScale;
    }>;
}